attributes.twig:
{
    "label": {{ data.title|json_encode|raw }},
    "type": {{ data.type|json_encode|raw }},
    "description": {{ data.notes|json_encode|raw }},
    "size": {{ data.size|json_encode|raw }},
{% if data.medium|length > 0 %}
{% if not data.medium is iterable %}
{% set media = data.medium|split(";") %}
{% else %}
{% set media = data.medium %}
{% endif %}
    "medium": {{ media|json_encode|raw }},
{% endif %}
    "rights": "All rights reserved.",
    "rights_statements": "In Copyright",
    "creator": [
        {
            "name_uri": "http:\/\/id.loc.gov\/authorities\/names\/n80070480",
            "role_uri": "http:\/\/id.loc.gov\/vocabulary\/relators\/art",
            "agent_type": "personal",
            "name_label": "Trimm, H. Wayne",
            "role_label": "Artist"
        }
    ],

    "photo_id": {{ "%04d"|format(data.id)|json_encode|raw }},
    "subject_loc": [
{% for subject in data_lod.subject.loc_subjects_thing %}
        {
            "uri": {{ subject.uri|json_encode|raw }},
            "label": {{ subject.label|json_encode|raw }}
        }
{% if not loop.last %},{% endif %}{% endfor %}
    ],
    "shelf_location": {{ data.shelf|json_encode|raw }},
    "date_created_edtf": {
        "date_to": "",
        "date_free": {{ data.created_edtf|json_encode|raw }},
        "date_from": "",
        "date_type": "date_free"
    },
    "date_created_free": {{ data.created|json_encode|raw }},
    "as:generator": {
        "type": "Create",
        "actor": {
            "url": {{ setURL|json_encode|raw }},
            "name": "ami",
            "type": "Service"
        },
        "endTime": "{{"now"|date("c")}}",
        "summary": "Generator",
        "@context": "https:\/\/www.w3.org\/ns\/activitystreams"
    }
}
